    // Return offset of chr in string (relative to 1)
    // Returns 0 if not found.
    uint32_t        W32Str_ChrInStr(
        W32CHR_T        chr,
        const
        W32CHR_T        *pStr
    )
    {
        uint32_t        index = 1;
        
        while (*pStr) {
            if (*pStr == chr) {
                return index;
            }
            ++pStr;
            ++index;
        }
        
        return 0;
    }
    
    
    
