    ERESULT         W32Str_CharFindNextW32(
        W32STR_DATA		*this,
        uint32_t        *pIndex,
        const
        W32CHR_T        chr
    )
    {
        uint32_t        i;
        uint32_t        index;
        uint32_t        lenStr;
        W32CHR_T        *pChr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !W32Str_Validate(this) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
        if (NULL == pIndex) {
            return ERESULT_INVALID_PARAMETER;
        }
#endif
        lenStr = W32Str_getLength(this);
        
        index = *pIndex;
        if (0 == index) {
            index = 1;
        }
        if (index > lenStr) {
            *pIndex = 0;
            return ERESULT_OUT_OF_RANGE;
        }

        pChr = array_Ptr((ARRAY_DATA *)this, index);
        for ( i=index; i<lenStr; ++i,++pChr ) {
            if (chr == *pChr) {
                *pIndex = i;
                return ERESULT_SUCCESS;
            }
        }
        
        // Return to caller.
        *pIndex = 0;
        return ERESULT_OUT_OF_RANGE;
    }
    

    
