    ERESULT         W32Str_CharFindPrevW32(
        W32STR_DATA		*cbp,
        uint32_t        *pIndex,
        const
        W32CHR_T        chr
    )
    {
        uint32_t        i;
        uint32_t        index;
        W32CHR_T        *pChr;
        uint32_t        lenStr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !W32Str_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
#endif
        lenStr = W32Str_getLength(cbp);
        
        if (NULL == pIndex) {
            return ERESULT_INVALID_PARAMETER;
        }
        index = *pIndex;
        if (0 == index) {
            index = lenStr;
        }
        if (index > lenStr) {
            return ERESULT_OUT_OF_RANGE;
        }
        
        pChr = array_Ptr((ARRAY_DATA *)cbp, index);
        for ( i=index; i; --i,--pChr ) {
            if (chr == *pChr) {
                *pIndex = i;
                return ERESULT_SUCCESS;
            }
        }
        
        // Return to caller.
        *pIndex = 0;
        return ERESULT_OUT_OF_RANGE;
    }
    
    
    
