    //---------------------------------------------------------------
    //                          I n i t
    //---------------------------------------------------------------

    W32STR_DATA *     W32Str_Init(
        W32STR_DATA       *this
    )
    {
        uint16_t        cbSize;
        W32CHR_T        eod = 0;
        ERESULT         eRc;
        
        if (OBJ_NIL == this) {
            return OBJ_NIL;
        }
        
        cbSize = obj_getSize(this);
        this = (W32STR_DATA *)array_Init((ARRAY_DATA *)this);
        if (OBJ_NIL == this) {
            return OBJ_NIL;
        }
        obj_setSize(this, cbSize);
        this->pSuperVtbl = obj_getVtbl(this);
        obj_setVtbl(this, (void *)&W32Str_Vtbl);
        array_setElemSize((ARRAY_DATA *)this, sizeof(W32CHR_T));
        array_setZeroNew((ARRAY_DATA *)this, false);

        // Set up the End-Of-Data marker (ie '\0')
        eRc = array_AppendData((ARRAY_DATA *)this, 1, &eod);
        if (ERESULT_HAS_FAILED(eRc)) {
            DEBUG_BREAK();
            obj_Release(this);
            return OBJ_NIL;
        }
        
    #ifdef NDEBUG
    #else
        if( !W32Str_Validate(this) ) {
            DEBUG_BREAK();
            obj_Release(this);
            return OBJ_NIL;
        }
        //BREAK_NOT_BOUNDARY4(&this->thread);
        BREAK_NOT_BOUNDARY4(sizeof(W32STR_DATA));
    #endif

        return this;
    }

     

