    ERESULT         W32Str_InsertA(
        W32STR_DATA		*cbp,
        uint32_t        offset,
        const
        char            *pStr
    )
    {
        ERESULT         eRc;
        uint32_t        i;
        uint32_t        len;
        W32CHR_T        *pInsert;
        W32CHR_T        chr;
        uint32_t        lenStr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !W32Str_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = W32Str_getLength(cbp);
        
        if( (0 == offset) || (offset > (lenStr + 1)) ) {
            return ERESULT_INVALID_PARAMETER;
        }
        if( (NULL == pStr) ) {
            return ERESULT_INVALID_PARAMETER;
        }
        
        len = (uint32_t)utf8_StrLenA(pStr);
        if (0 == len) {
            return ERESULT_DATA_NOT_FOUND;
        }
        
        eRc =   array_InsertSpacing(
                        (ARRAY_DATA *)cbp,
                        offset,
                        len
                );
        
        // Copy the data.
        if (ERESULT_IS_SUCCESSFUL(eRc)) {
            pInsert = array_Ptr((ARRAY_DATA *)cbp, offset);
            for (i=0; i<len; ++i) {
                chr = utf8_Utf8ToW32_Scan(&pStr);
                *pInsert++ = chr;
            }
        }
        
        // Return to caller.
        return ERESULT_SUCCESS;
    }
    
    
    
