    ASTR_DATA *     W32Str_ToChrCon(
        W32STR_DATA       *this
    )
    {
        const
        W32CHR_T        *pData;
        W32CHR_T        wchr;
        ASTR_DATA       *pWrkStr;
        char            work[16];
        
        if (OBJ_NIL == this) {
            return OBJ_NIL;
        }
        
        pWrkStr = AStr_New();
        if (OBJ_NIL == pWrkStr) {
            return pWrkStr;
        }
        
        pData = W32Str_getData(this);
        while ((wchr = *pData++)) {
            utf8_W32ToChrCon(wchr, work);
            AStr_AppendA(pWrkStr, work);
        }
        
        
        return pWrkStr;
    }
    
    
    
