    //---------------------------------------------------------------
    //                     T o  U t f 8  F i l e
    //---------------------------------------------------------------
    
    ERESULT         W32Str_ToUtf8File(
        W32STR_DATA		*this,
        PATH_DATA       *pPath
    )
    {
        FILE            *pFile = NULL;
        uint32_t        size;
        char            *pChr;
        char            *pBuffer;
        int             chr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !W32Str_Validate(this) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
        if( OBJ_NIL == pPath ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_PARAMETER;
        }
#endif
        
        pBuffer = W32Str_CStringA(this, &size);
        pChr = pBuffer;

        pFile = fopen(Path_getData(pPath), "wb");
        if (NULL == pFile) {
            return ERESULT_FILE_NOT_FOUND;
        }
        while ( size-- ) {
            chr = fputc(*pChr++,pFile);
            if (EOF == chr) {
                DEBUG_BREAK();
                return ERESULT_IO_ERROR;
            }
        }
        fclose(pFile);
        pFile = NULL;
        
        mem_Free(pBuffer);
        pBuffer = NULL;
        
        // Return to caller.
        return ERESULT_SUCCESS;
    }
    
    
    
    //---------------------------------------------------------------
    //                       T o  S t r i n g
    //---------------------------------------------------------------
    
    ASTR_DATA * W32Str_ToDebugString(
        W32STR_DATA       *this,
        int             indent
    )
    {
        char            str[256];
        int             j;
        ASTR_DATA       *pStr;
        ASTR_DATA       *pWrkStr;
        
        if (OBJ_NIL == this) {
            return OBJ_NIL;
        }
        
        pStr = AStr_New();
        if (indent) {
            AStr_AppendCharRepeatW32(pStr, indent, ' ');
        }
        str[0] = '\0';
        j = snprintf(
                     str,
                     sizeof(str),
                     "{%p(WStr)\n",
                     this
                     );
        AStr_AppendA(pStr, str);
        
        pWrkStr = array_ToDebugString( &this->super, indent+3 );
        if (pWrkStr) {
            AStr_Append(pStr, pWrkStr);
            obj_Release(pWrkStr);
        }
        
        j = snprintf(str, sizeof(str), " %p(WStr)}\n", this);
        AStr_AppendA(pStr, str);
        
        return pStr;
    }
    

    
    //---------------------------------------------------------------
    //                          T r i m
    //---------------------------------------------------------------
    
    ERESULT         W32Str_Trim(
        W32STR_DATA		*this
    )
    {
        ERESULT         eRc;
        uint32_t        i;
        uint32_t        j;
        uint32_t        lenStr;
        W32CHR_T        *pData;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !W32Str_Validate(this) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = W32Str_getLength(this);
        if ( 0 == lenStr ) {
            return ERESULT_SUCCESS;
        }

        // Remove leading characters.
        pData = (W32CHR_T *)W32Str_getData(this);
        j = 0;
        for (i=0; i<lenStr; ++i) {
            if (!ascii_isWhiteSpaceW32(*pData))
                break;
            ++j;
            ++pData;
        }
        if (j) {
            eRc = W32Str_Remove(this, 1, j);
        }
        
        lenStr = W32Str_getLength(this);
        if ( 0 == lenStr ) {
            return ERESULT_SUCCESS;
        }
        
        // Remove trailing characters.
        pData = (W32CHR_T *)W32Str_getData(this);
        j = 0;
        for (i=lenStr; i; --i) {
            if (!ascii_isWhiteSpaceW32(*(pData+i-1)))
                break;
            ++j;
        }
        if (j) {
            eRc = W32Str_Truncate(this, (lenStr - j));
        }
        
        // Return to caller.
        return ERESULT_SUCCESS;
    }
    
    
    
    //---------------------------------------------------------------
    //                       T r u n c a t e
    //---------------------------------------------------------------
    
    ERESULT         W32Str_Truncate(
        W32STR_DATA		*this,
        uint32_t        len
    )
    {
        uint32_t        lenStr;
        W32CHR_T        data = 0;
        ERESULT         eRc;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !W32Str_Validate( this ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = W32Str_getLength(this);
        if (len > lenStr) {
            return ERESULT_INVALID_PARAMETER;
        }
        
        // Insert new EOD char and adjust size.
        eRc = array_Put((ARRAY_DATA *)this, (len + 1), 1, &data);
        BREAK_FAILED(eRc);
        array_setSize((ARRAY_DATA *)this, (len + 1));
        
        // Return to caller.
        return ERESULT_SUCCESS;
    }
    
    
    
