    // Return offset of chr in string (relative to 1)
    // Returns 0 if not found.
    uint32_t        WStr_ChrInStr(
        int32_t         chr,
        const
        int32_t         *pStr
    )
    {
        uint32_t        index = 1;
        
        while (*pStr) {
            if (*pStr == chr) {
                return index;
            }
            ++pStr;
            ++index;
        }
        
        return 0;
    }
    
    
    
    ERESULT         WStr_Cmp(
        const
        int32_t         *pStr1,
        const
        int32_t         *pStr2
    )
    {
        ERESULT         eRc = ERESULT_SUCCESSFUL_COMPLETION;
        int             i;
        
        for( ;; ) {
            if( *pStr1 )
                ;
            else {
                if( *pStr2 )
                    eRc = ERESULT_SUCCESS_LESS_THAN;
                break;
            }
            if( *pStr2 )
                ;
            else {
                if( *pStr1 )
                    eRc = ERESULT_SUCCESS_GREATER_THAN;
                break;
            }
            i = *pStr1 - *pStr2;
            if( i ) {
                if( i < 0 )
                    eRc = ERESULT_SUCCESS_LESS_THAN;
                else
                    eRc = ERESULT_SUCCESS_GREATER_THAN;
                break;
            }
            ++pStr1;
            ++pStr2;
        }
        
        
        // Return to caller.
        return eRc;
    }
    
    
    
