    ERESULT         WStr_AppendCharW(
        WSTR_DATA		*cbp,
        uint32_t        len,
        const
        int32_t         chr
    )
    {
        int32_t         *pData;
        uint32_t        offset;
        uint32_t        i;
        ERESULT         eRc;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
        if (0 == len) {
            return ERESULT_INVALID_PARAMETER;
        }
#endif
        
        offset = pwr2Array_getSize((PWR2ARRAY_DATA *)cbp);
        eRc =   pwr2Array_InsertSpacing(
                        (PWR2ARRAY_DATA *)cbp,
                        offset,
                        len
                );
        if (ERESULT_IS_SUCCESSFUL(eRc)) {
            pData = pwr2Array_Ptr( (PWR2ARRAY_DATA *)cbp, offset);
            for (i=0; i<len; ++i) {
                *pData++ = chr;
            };
            //*pData = '\0';
        }
        
        // Return to caller.
        return eRc;
    }
    
    
    
