    //---------------------------------------------------------------
    
    ERESULT         WStr_AppendUtf8File(
        WSTR_DATA		*this,
        PATH_DATA       *pPath
    )
    {
        ERESULT         eRc = ERESULT_GENERAL_FAILURE;
        wint_t          chr;
        FILE            *pFile;
        
        /* Do Initialization.
         */
#ifdef NDEBUG
#else
        if( !WStr_Validate(this) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
        if( OBJ_NIL == pPath ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_PARAMETER;
        }
#endif
        
        pFile = fopen(path_getData(pPath), "r");
        if (NULL == pFile) {
            return ERESULT_FILE_NOT_FOUND;
        }
        while ( (chr = fgetwc(pFile)) != EOF ) {
            eRc = WStr_AppendCharW(this, 1, chr);
            if (ERESULT_FAILED(eRc)) {
                DEBUG_BREAK();
                return eRc;
            }
        }
        fclose(pFile);
        pFile = NULL;
        
        return eRc;
    }
    
    
    
    //---------------------------------------------------------------
    //                    A p p e n d  P r i n t
    //---------------------------------------------------------------
    
    ERESULT         WStr_AppendPrint(
        WSTR_DATA		*this,
        const
        char            *pFormat,
        ...
    )
    {
        ERESULT         eRc;
        int             size;
        va_list         arg_ptr;
        char            *pStr = NULL;
        
        /* Do Initialization.
         */
#ifdef NDEBUG
#else
        if( !WStr_Validate(this) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
#endif
        
        va_start( arg_ptr, pFormat );
        size = vsnprintf( NULL, 0, pFormat, arg_ptr );
        va_end( arg_ptr );
        ++size;
        pStr = (char *)mem_Malloc( size );
        if( pStr == NULL ) {
            return ERESULT_INSUFFICIENT_MEMORY;
        }
        va_start( arg_ptr, pFormat );
        size = vsnprintf( pStr, size, pFormat, arg_ptr );
        va_end( arg_ptr );
        eRc = WStr_AppendA(this, pStr);
        mem_Free( pStr );
        pStr = NULL;
        
        return eRc;
    }
    
    
    
