    //---------------------------------------------------------------
    
    ERESULT         WStr_CharFindNextW(
        WSTR_DATA		*cbp,
        uint32_t        *pIndex,
        const
        int32_t         chr
    )
    {
        uint32_t        i;
        uint32_t        index;
        uint32_t        lenStr;
        int32_t         *pChr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
        if (NULL == pIndex) {
            return ERESULT_INVALID_PARAMETER;
        }
#endif
        lenStr = WStr_getLength(cbp);
        
        index = *pIndex;
        if (0 == index) {
            index = 1;
        }
        if (index > lenStr) {
            *pIndex = 0;
            return ERESULT_OUT_OF_RANGE;
        }

        pChr = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp,index);
        for ( i=index; i<lenStr; ++i,++pChr ) {
            if (chr == *pChr) {
                *pIndex = i;
                return ERESULT_SUCCESSFUL_COMPLETION;
            }
        }
        
        // Return to caller.
        *pIndex = 0;
        return ERESULT_OUT_OF_RANGE;
    }
    

    
    //---------------------------------------------------------------
    //               C h a r  F i n d  P r e v
    //---------------------------------------------------------------
    
    ERESULT         WStr_CharFindPrevW(
        WSTR_DATA		*cbp,
        uint32_t        *pIndex,
        const
        int32_t         chr
    )
    {
        uint32_t        i;
        uint32_t        index;
        int32_t         *pChr;
        uint32_t        lenStr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
#endif
        lenStr = WStr_getLength(cbp);
        
        if (NULL == pIndex) {
            return ERESULT_INVALID_PARAMETER;
        }
        index = *pIndex;
        if (0 == index) {
            index = lenStr;
        }
        if (index > lenStr) {
            return ERESULT_OUT_OF_RANGE;
        }
        
        pChr = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp, index);
        for ( i=index; i; --i,--pChr ) {
            if (chr == *pChr) {
                *pIndex = i;
                return ERESULT_SUCCESSFUL_COMPLETION;
            }
        }
        
        // Return to caller.
        *pIndex = 0;
        return ERESULT_OUT_OF_RANGE;
    }
    
    
    
