    //---------------------------------------------------------------
    //                       C l e a n  D i r
    //---------------------------------------------------------------
    
    ERESULT         WStr_CleanDir(
        WSTR_DATA		*cbp
    )
    {
        //uint32_t        i;
        //uint32_t        *pChr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return false;
        }
#endif
        if (0 == pwr2Array_getSize((PWR2ARRAY_DATA *)cbp)) {
            return ERESULT_SUCCESSFUL_COMPLETION;
        }
        
#ifdef XYZZY        //TODO: Fix this!
        for (i=0,pChr=cbp->pData; i<pwr2Array_getSize((PWR2ARRAY_DATA *)cbp); ++i,++pChr) {
            if ((cbp->length - i) >= 2) {
                if( (*pChr == '/') && (*(pChr+1) == '/') ) {
                    memmove(pChr, pChr+1, (cbp->length - i - 1));
                    --cbp->length;
                    continue;
                }
            }
            if ((cbp->length - i) >= 3) {
                if( (*pChr == '/') && (*(pChr+1) == '.') && (*(pChr+2) == '/') ) {
                    memmove(pChr, pChr+2, (cbp->length - i - 2));
                    --cbp->length;
                    --cbp->length;
                    continue;
                }
            }
        }
        
        if( (cbp->length >= 3)
            && (cbp->pData[cbp->length-3] == '/')
            && (cbp->pData[cbp->length-2] == '.')
            && (cbp->pData[cbp->length-1] == '/')
        ) {
            memmove(pChr, pChr+2, (cbp->length - i - 2));
            --cbp->length;
            --cbp->length;
        }
        
        while ( (cbp->pData[cbp->length-1] == '/') && (cbp->length > 1) ) {
            --cbp->length;
        }
        cbp->pData[cbp->length] = '\0';
#endif
        
        // Return to caller.
        return ERESULT_SUCCESSFUL_COMPLETION;
    }
    
    
    
