    ERESULT         WStr_CompareA(
        WSTR_DATA		*cbp,
        const
        char            *pString
    )
    {
        ERESULT         eRc = ERESULT_SUCCESSFUL_COMPLETION;
        uint32_t        str1len;
        const
        int32_t         *pStr1;
        uint32_t        str2len;
        uint32_t        chr2;
        int             i;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
#endif
        
        str1len = WStr_getLength(cbp);
        pStr1 = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp,1);
        if (NULL == pString) {
            str2len = 0;
        }
        else {
            str2len = (uint32_t)utf8_StrLenA(pString);
        }
        
        for( ;; ) {
            chr2 = utf8_Utf8ToWC_Scan(&pString);
            if( *pStr1 )
                ;
            else {
                if( chr2 )
                    eRc = ERESULT_SUCCESS_LESS_THAN;
                break;
            }
            if( chr2 )
                ;
            else {
                if( *pStr1 )
                    eRc = ERESULT_SUCCESS_GREATER_THAN;
                break;
            }
            i = *pStr1 - chr2;
            if( i ) {
                if( i < 0 )
                    eRc = ERESULT_SUCCESS_LESS_THAN;
                else
                    eRc = ERESULT_SUCCESS_GREATER_THAN;
                break;
            }
            ++pStr1;
        }
        
        
        // Return to caller.
        return eRc;
    }
    
    
    ERESULT         WStr_CompareW(
        WSTR_DATA		*cbp,
        const
        int32_t         *pString
    )
    {
        ERESULT         eRc = ERESULT_SUCCESSFUL_COMPLETION;
        uint32_t        str1len;
        const
        int32_t         *pStr1;
        uint32_t        str2len;
        const
        int32_t         *pStr2;
        int             i;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
#endif
        
        str1len = WStr_getLength(cbp);
        pStr1 = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp,1);
        if (NULL == pString) {
            str2len = 0;
            pStr2 = NULL;
        }
        else {
            str2len = (uint32_t)WStr_StrLen(pString);
            pStr2 = pString;
        }
        
        if ((0 == str1len) && (0 == str2len)) {
            return ERESULT_SUCCESSFUL_COMPLETION;
        }
        if ((0 == str1len) && (0 < str2len)) {
            return ERESULT_SUCCESS_LESS_THAN;
        }
        if ((0 < str1len) && (0 == str2len)) {
            return ERESULT_SUCCESS_GREATER_THAN;
        }
        
        for( ;; ) {
            if( *pStr1 )
                ;
            else {
                if( *pStr2 )
                    eRc = ERESULT_SUCCESS_LESS_THAN;
                break;
            }
            if( *pStr2 )
                ;
            else {
                if( *pStr1 )
                    eRc = ERESULT_SUCCESS_GREATER_THAN;
                break;
            }
            i = *pStr1 - *pStr2;
            if( i ) {
                if( i < 0 )
                    eRc = ERESULT_SUCCESS_LESS_THAN;
                else
                    eRc = ERESULT_SUCCESS_GREATER_THAN;
                break;
            }
            ++pStr1;
            ++pStr2;
        }
        
        
        // Return to caller.
        return eRc;
    }
    
    
    
