    //---------------------------------------------------------------
    //                     E s c a p e  F o r  C
    //---------------------------------------------------------------
    
    ERESULT         WStr_EscapeForC(
        WSTR_DATA		*this
    )
    {
        ERESULT         eRc = ERESULT_SUCCESS;
        int32_t         *pData;
        uint32_t        lenStr;
        uint32_t        i;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( this ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(this);
        
        // The order matters in how we do these replacements.
       
        for (i=1; i<=WStr_getLength(this); ++i) {
            pData = pwr2Array_Ptr((PWR2ARRAY_DATA *)this, i);
            if (pData) {
                if (*pData == '\\') {
                    pwr2Array_InsertData((PWR2ARRAY_DATA *)this, i, 1, L"\\");
                    i += 1;
                }
            }
        }
        
        for (i=1; i<=WStr_getLength(this); ++i) {
            pData = pwr2Array_Ptr((PWR2ARRAY_DATA *)this, i);
            if (pData) {
                if (*pData == '"') {
                    pwr2Array_InsertData((PWR2ARRAY_DATA *)this, i, 1, L"\\");
                    i += 1;
                }
                else if (*pData == '\n') {
                    *pData = 'n';
                    pwr2Array_InsertData((PWR2ARRAY_DATA *)this, i, 1, L"\\");
                    i += 1;
                }
                else if (*pData == '\r') {
                    *pData = 'r';
                    pwr2Array_InsertData((PWR2ARRAY_DATA *)this, i, 1, L"\\");
                    i += 1;
                }
                else if (*pData == '\t') {
                    *pData = 't';
                    pwr2Array_InsertData((PWR2ARRAY_DATA *)this, i, 1, L"\\");
                    i += 1;
                }
            }
        }
        
        // Return to caller.
        return eRc;
    }
    
    
    
    
    
