    //---------------------------------------------------------------
    //                      I n d e x  U n t i l
    //---------------------------------------------------------------
    
    ERESULT         WStr_IndexUntil(
        WSTR_DATA		*cbp,
        const
        int32_t         *pSetStr,
        uint32_t        *pIndex
    )
    {
        uint32_t        i;
        uint32_t        index;
        uint32_t        lenStr;
        int32_t         *pChr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
        if (NULL == pIndex) {
            return ERESULT_INVALID_PARAMETER;
        }
        if (NULL == pSetStr) {
            return ERESULT_INVALID_PARAMETER;
        }
#endif
        lenStr = WStr_getLength(cbp);
        
        index = *pIndex;
        if (0 == index) {
            index = 1;
        }
        if (index > lenStr) {
            *pIndex = 0;
            return ERESULT_OUT_OF_RANGE;
        }
        
        pChr = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp,index);
        for ( i=index; i<lenStr; ++i,++pChr ) {
            if (WStr_ChrInStr(*pChr,pSetStr)) {
                *pIndex = i;
                return ERESULT_SUCCESSFUL_COMPLETION;
            }
        }
        
        // Return to caller.
        *pIndex = 0;
        return ERESULT_OUT_OF_RANGE;
    }
    
    
    
    //---------------------------------------------------------------
    //                          I n i t
    //---------------------------------------------------------------

    WSTR_DATA *     WStr_Init(
        WSTR_DATA       *cbp
    )
    {
        uint16_t        cbSize;
        int32_t         eod = 0;
        ERESULT         eRc;
        
        if (OBJ_NIL == cbp) {
            return OBJ_NIL;
        }
        
        cbSize = obj_getSize(cbp);
        cbp = (WSTR_DATA *)pwr2Array_Init((PWR2ARRAY_DATA *)cbp, 2);
        if (OBJ_NIL == cbp) {
            return OBJ_NIL;
        }
        obj_setSize(cbp, cbSize);
        obj_setIdent((OBJ_ID)cbp, OBJ_IDENT_WSTR);
        obj_setVtbl(cbp, (void *)&WStr_Vtbl);
        pwr2Array_setDontZero((PWR2ARRAY_DATA *)cbp, true);

        // Set up the End-Of-Data marker (ie '\0')
        eRc = pwr2Array_AppendData((PWR2ARRAY_DATA *)cbp, 1, &eod);
        if (ERESULT_HAS_FAILED(eRc)) {
            DEBUG_BREAK();
            obj_Release(cbp);
            return OBJ_NIL;
        }
        
    #ifdef NDEBUG
    #else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            obj_Release(cbp);
            return OBJ_NIL;
        }
        //BREAK_NOT_BOUNDARY4(&cbp->thread);
    #endif

        return cbp;
    }

     

    //---------------------------------------------------------------
    //                       I n s e r t
    //---------------------------------------------------------------
    
    ERESULT         WStr_InsertA(
        WSTR_DATA		*cbp,
        uint32_t        offset,
        const
        char            *pStr
    )
    {
        ERESULT         eRc;
        uint32_t        i;
        uint32_t        len;
        int32_t         *pInsert;
        int32_t         chr;
        uint32_t        lenStr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(cbp);
        
        if( (0 == offset) || (offset > (lenStr + 1)) ) {
            return ERESULT_INVALID_PARAMETER;
        }
        if( (NULL == pStr) ) {
            return ERESULT_INVALID_PARAMETER;
        }
        
        len = (uint32_t)utf8_StrLenA(pStr);
        if (0 == len) {
            return ERESULT_DATA_NOT_FOUND;
        }
        
        eRc =   pwr2Array_InsertSpacing(
                        (PWR2ARRAY_DATA *)cbp,
                        offset,
                        len
                );
        
        // Copy the data.
        if (ERESULT_IS_SUCCESSFUL(eRc)) {
            pInsert = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp, offset);
            for (i=0; i<len; ++i) {
                chr = utf8_Utf8ToWC_Scan(&pStr);
                *pInsert++ = chr;
            }
        }
        
        // Return to caller.
        return ERESULT_SUCCESSFUL_COMPLETION;
    }
    
    
    
