    //---------------------------------------------------------------
    
    ERESULT         WStr_IsOnlyASCII(
        WSTR_DATA		*cbp
    )
    {
        ERESULT         eRc = ERESULT_SUCCESSFUL_COMPLETION;
        int32_t         *pData;
        uint32_t        lenStr;
    
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(cbp);
        
        pData = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp, 1);
        if (pData) {
            while (*pData && lenStr--) {
                if (*pData > 0x7F) {
                    eRc = ERESULT_GENERAL_FAILURE;
                    break;
                }
                ++pData;
            }
        }
        
        // Return to caller.
        return eRc;
    }





    //---------------------------------------------------------------
    //                          L e f t
    //---------------------------------------------------------------
    
    ERESULT         WStr_Left(
        WSTR_DATA		*cbp,
        uint32_t        len,
        WSTR_DATA       **ppOther           // [out]
    )
    {
        ERESULT         eRc;
        uint32_t        offset = 0;
        uint32_t        lenStr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(cbp);
        
        if (NULL == ppOther) {
            return ERESULT_INVALID_PARAMETER;
        }
        if( (len > lenStr) ) {
            return ERESULT_INVALID_PARAMETER;
        }
        if ((0 == lenStr) || ((offset + len - 1) > lenStr)) {
            return ERESULT_OUT_OF_RANGE;
        }
        
        eRc = WStr_Mid(cbp, 1, len, ppOther);
        
        // Return to caller.
        return eRc;
    }
    
    
    
    //---------------------------------------------------------------
    //                       L o w e r
    //---------------------------------------------------------------
    
    ERESULT         WStr_Lower(
        WSTR_DATA		*cbp
    )
    {
        int32_t         *pData;
        uint32_t        lenStr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif

        lenStr = WStr_getLength(cbp);
        pData  = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp, 1);
        if (lenStr) {
            while( *pData && lenStr-- ) {
                if( (*pData >= 'A') && (*pData <= 'Z') )
                    *pData = (char)(*pData + ('a' - 'A'));
                ++pData;
            }
        }
        
        // Return to caller.
        return ERESULT_SUCCESSFUL_COMPLETION;
    }
    
    
    
    //---------------------------------------------------------------
    //                          M i d
    //---------------------------------------------------------------
    
    ERESULT         WStr_Mid(
        WSTR_DATA		*cbp,
        uint32_t        offset,
        uint32_t        len,
        WSTR_DATA       **ppOther           // [out]
    )
    {
        WSTR_DATA       *pNew;
        ERESULT         eRc;
        uint32_t        lenStr;
        int32_t         *pData;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(cbp);

        if (NULL == ppOther) {
            return ERESULT_INVALID_PARAMETER;
        }
        else {
            *ppOther = OBJ_NIL;
        }
        if( (offset > lenStr) ) {
            return ERESULT_INVALID_PARAMETER;
        }
        if ((0 == lenStr) || ((offset + len - 1) > lenStr)) {
            return ERESULT_OUT_OF_RANGE;
        }
        
        pNew = WStr_Alloc();
        pNew = WStr_Init(pNew);
        if (OBJ_NIL == pNew) {
            return ERESULT_INSUFFICIENT_MEMORY;
        }
        pData  = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp, offset);
        if (pData) {
            eRc = WStr_AppendW(pNew, len, pData);
        }
        else {
            return ERESULT_OUT_OF_RANGE;
        }
        
        // Return to caller.
        *ppOther = pNew;
        return ERESULT_SUCCESS;
    }
    

    
    //---------------------------------------------------------------
    //                          R e m o v e
    //---------------------------------------------------------------
    
    ERESULT         WStr_Remove(
        WSTR_DATA		*cbp,
        uint32_t        offset,
        uint32_t        len
    )
    {
        uint32_t        lenStr;
        ERESULT         eRc;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(cbp);
        if( (0 == offset) || (offset > lenStr) ) {
            return ERESULT_INVALID_PARAMETER;
        }
        if ((0 == lenStr) || ((offset + len - 1) > lenStr)) {
            return ERESULT_OUT_OF_RANGE;
        }

        eRc = pwr2Array_Delete((PWR2ARRAY_DATA *)cbp, offset, len);
        
        // Return to caller.
        return eRc;
    }
    
    
    
    //---------------------------------------------------------------
    //                          R i g h t
    //---------------------------------------------------------------
    
    ERESULT         WStr_Right(
        WSTR_DATA		*cbp,
        uint32_t        len,
        WSTR_DATA       **ppOther           // [out]
    )
    {
        ERESULT         eRc;
        uint32_t        offset;
        uint32_t        lenStr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(cbp);
        if (NULL == ppOther) {
            return ERESULT_INVALID_PARAMETER;
        }
        offset = lenStr - len + 1;
        if( (offset > lenStr) ) {
            return ERESULT_INVALID_PARAMETER;
        }
        if ((0 == lenStr) || ((offset + len - 1) > lenStr)) {
            return ERESULT_OUT_OF_RANGE;
        }
        
        eRc = WStr_Mid(cbp, offset, len, ppOther);
        
        // Return to caller.
        return eRc;
    }
    
    
    
