    //---------------------------------------------------------------
    //                           S p a n
    //---------------------------------------------------------------
    
    ERESULT         WStr_SpanW(
        WSTR_DATA		*cbp,
        uint32_t        *pIndex,
        const
        int32_t         *pSetStr
    )
    {
        uint32_t        i;
        uint32_t        index;
        uint32_t        lenStr;
        int32_t         *pChr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( cbp ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
        if (NULL == pIndex) {
            return ERESULT_INVALID_PARAMETER;
        }
        if (NULL == pSetStr) {
            return ERESULT_INVALID_PARAMETER;
        }
#endif
        lenStr = WStr_getLength(cbp);
        
        index = *pIndex;
        if (0 == index) {
            index = 1;
        }
        if (index > lenStr) {
            *pIndex = 0;
            return ERESULT_OUT_OF_RANGE;
        }
        
        pChr = pwr2Array_Ptr((PWR2ARRAY_DATA *)cbp,index);
        for ( i=index; i<lenStr; ++i,++pChr ) {
            if (WStr_ChrInStr(*pChr,pSetStr))
                ;
            else {
                *pIndex = i;
                return ERESULT_SUCCESS;
            }
        }
        
        // Return to caller.
        *pIndex = 0;
        return ERESULT_OUT_OF_RANGE;
    }
    
    
    
    //---------------------------------------------------------------
    //                       T o  C h r  C o n
    //---------------------------------------------------------------
    
    ASTR_DATA *     WStr_ToChrCon(
        WSTR_DATA       *this
    )
    {
        const
        int32_t         *pData;
        int32_t         wchr;
        ASTR_DATA       *pWrkStr;
        
        if (OBJ_NIL == this) {
            return OBJ_NIL;
        }
        
        pWrkStr = AStr_New();
        if (OBJ_NIL == pWrkStr) {
            return pWrkStr;
        }
        
        pData = WStr_getData(this);
        while ((wchr = *pData)) {
            if (ascii_isAsciiW(wchr) && ascii_isPrintableA(wchr)) {
                AStr_AppendCharA(pWrkStr, wchr);
            }
            else if (wchr < 256) {
                switch (wchr) {
                        
                    case 9:
                        AStr_AppendA(pWrkStr, "\\t");
                        break;
                        
                    case 10:
                        AStr_AppendA(pWrkStr, "\\n");
                        break;
                        
                    case 12:
                        AStr_AppendA(pWrkStr, "\\f");
                        break;
                        
                    case 13:
                        AStr_AppendA(pWrkStr, "\\r");
                        break;
                        
                    default:
                        AStr_AppendA(pWrkStr, "\\x");
                        AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 4) & 0xF));
                        AStr_AppendCharA(pWrkStr, hex_DigitToChrA(wchr & 0xF));
                        break;
                }
            }
            else if (wchr < 65236) {
                AStr_AppendA(pWrkStr, "\\u");
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 12) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 8) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 4) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA(wchr & 0xF));
            }
            else {
                AStr_AppendA(pWrkStr, "\\U");
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 28) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 24) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 20) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 16) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 12) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 8) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA((wchr >> 4) & 0xF));
                AStr_AppendCharA(pWrkStr, hex_DigitToChrA(wchr & 0xF));
            }
            ++pData;
        }
        
        
        return pWrkStr;
    }
    
    
    
