    ASTR_DATA *     WStr_ToJSON(
        WSTR_DATA       *this
    )
    {
        ASTR_DATA       *pStr;
        ASTR_DATA       *pWrkStr;
        const
        OBJ_INFO        *pInfo;
        
#ifdef NDEBUG
#else
        if( !WStr_Validate( this ) ) {
            DEBUG_BREAK();
            return OBJ_NIL;
        }
#endif
        pInfo = obj_getInfo(this);
        pWrkStr = WStr_ToChrCon(this);
        if (pWrkStr == OBJ_NIL) {
            return OBJ_NIL;
        }
        
        pStr = AStr_New();
        if (pWrkStr == OBJ_NIL) {
            obj_Release(pWrkStr);
            return OBJ_NIL;
        }
        AStr_AppendA(pStr, "{\"objectType\":\"");
        AStr_AppendA(pStr, pInfo->pClassName);
        AStr_AppendA(pStr, "\",\"data\":\"");
        AStr_Append(pStr, pWrkStr);
        AStr_AppendA(pStr, "\"}\n");
        obj_Release(pWrkStr);
        
        return pStr;
    }
    
    
    
    //---------------------------------------------------------------
    //                     T o  U t f 8  F i l e
    //---------------------------------------------------------------
    
    ERESULT         WStr_ToUtf8File(
        WSTR_DATA		*this,
        PATH_DATA       *pPath
    )
    {
        FILE            *pFile = NULL;
        uint32_t        size;
        char            *pChr;
        char            *pBuffer;
        int             chr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate(this) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_OBJECT;
        }
        if( OBJ_NIL == pPath ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_PARAMETER;
        }
#endif
        
        pBuffer = WStr_CStringA(this, &size);
        pChr = pBuffer;

        pFile = fopen(path_getData(pPath), "wb");
        if (NULL == pFile) {
            return ERESULT_FILE_NOT_FOUND;
        }
        while ( size-- ) {
            chr = fputc(*pChr++,pFile);
            if (EOF == chr) {
                DEBUG_BREAK();
                return ERESULT_IO_ERROR;
            }
        }
        fclose(pFile);
        pFile = NULL;
        
        mem_Free(pBuffer);
        pBuffer = NULL;
        
        // Return to caller.
        return ERESULT_SUCCESS;
    }
    
    
    
    //---------------------------------------------------------------
    //                       T o  S t r i n g
    //---------------------------------------------------------------
    
    ASTR_DATA * WStr_ToDebugString(
        WSTR_DATA       *this,
        int             indent
    )
    {
        char            str[256];
        int             j;
        ASTR_DATA       *pStr;
        ASTR_DATA       *pWrkStr;
        
        if (OBJ_NIL == this) {
            return OBJ_NIL;
        }
        
        pStr = AStr_New();
        if (indent) {
            AStr_AppendCharRepeatW(pStr, indent, ' ');
        }
        str[0] = '\0';
        j = snprintf(
                     str,
                     sizeof(str),
                     "{%p(WStr)\n",
                     this
                     );
        AStr_AppendA(pStr, str);
        
        pWrkStr = pwr2Array_ToDebugString( &this->super, indent+3 );
        if (pWrkStr) {
            AStr_Append(pStr, pWrkStr);
            obj_Release(pWrkStr);
        }
        
        j = snprintf(str, sizeof(str), " %p(WStr)}\n", this);
        AStr_AppendA(pStr, str);
        
        return pStr;
    }
    

    
    //---------------------------------------------------------------
    //                          T r i m
    //---------------------------------------------------------------
    
    ERESULT         WStr_Trim(
        WSTR_DATA		*this
    )
    {
        ERESULT         eRc;
        uint32_t        i;
        uint32_t        j;
        uint32_t        k;
        uint32_t        lenStr;
        int32_t         *pData;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate(this) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(this);
        if ( 0 == lenStr ) {
            return ERESULT_SUCCESS;
        }

        // Remove leading characters.
        pData = (int32_t *)WStr_getData(this);
        k = 0;
        for (i=1; i<=lenStr; ++i) {
            j = WStr_ChrInStr(*pData, whiteSpaceW);
            if (0 == j) {
                break;
            }
            ++k;
            ++pData;
        }
        if (k) {
            eRc = WStr_Remove(this, 1, k);
        }
        
        lenStr = WStr_getLength(this);
        if ( 0 == lenStr ) {
            return ERESULT_SUCCESSFUL_COMPLETION;
        }
        
        // Remove trailing characters.
        pData = (int32_t *)WStr_getData(this);
        k = 0;
        for (i=lenStr; i; --i) {
            j = WStr_ChrInStr(pData[i-1], whiteSpaceW);
            if (0 == j) {
                break;
            }
            ++k;
        }
        if (k) {
            eRc = WStr_Truncate(this, (lenStr - k));
        }
        
        // Return to caller.
        return ERESULT_SUCCESS;
    }
    
    
    
    //---------------------------------------------------------------
    //                       T r u n c a t e
    //---------------------------------------------------------------
    
    ERESULT         WStr_Truncate(
        WSTR_DATA		*this,
        uint32_t        len
    )
    {
        uint32_t        lenStr;
        int32_t         *pData;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate( this ) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(this);
        if (len > lenStr) {
            return ERESULT_INVALID_PARAMETER;
        }
        
        // Insert new EOD char and adjust size.
        pData  = pwr2Array_Ptr((PWR2ARRAY_DATA *)this, (len + 1));
        if (pData) {
            *pData = '\0';
        }
        pwr2Array_setSize((PWR2ARRAY_DATA *)this, (len + 1));
        
        // Return to caller.
        return ERESULT_SUCCESS;
    }
    
    
    
    //---------------------------------------------------------------
    //                       U p p e r
    //---------------------------------------------------------------
    
    ERESULT         WStr_Upper(
        WSTR_DATA		*this
    )
    {
        int32_t         *pData;
        uint32_t        lenStr;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !WStr_Validate(this) ) {
            DEBUG_BREAK();
            return ERESULT_INVALID_DATA;
        }
#endif
        lenStr = WStr_getLength(this);
        
        if (lenStr) {
            pData = pwr2Array_Ptr((PWR2ARRAY_DATA *)this, 1);
            while( *pData && lenStr-- ) {
                if( (*pData >= 'a') && (*pData <= 'z') )
                    *pData = (char)(*pData - ('a' - 'A'));
                ++pData;
            }
        }
        
        // Return to caller.
        return ERESULT_SUCCESS;
    }
    
    
    
    //---------------------------------------------------------------
    //                      V a l i d a t e
    //---------------------------------------------------------------

    #ifdef NDEBUG
    #else
    bool            WStr_Validate(
        WSTR_DATA      *cbp
    )
    {
        if( cbp ) {
            if ( obj_IsKindOf(cbp, OBJ_IDENT_WSTR) )
                ;
            else
                return false;
        }
        else
            return false;
        if( !(obj_getSize(cbp) >= sizeof(WSTR_DATA)) )
            return false;

        // Return to caller.
        return true;
    }
    #endif


    
    
    
