    FILE_DATA *     file_Alloc(
    )
    {
        FILE_DATA       *this;
        uint32_t        cbSize = sizeof(FILE_DATA);
        
        // Do initialization.
        
        this = obj_Alloc( cbSize );
        
        // Return to caller.
        return this;
    }



    FILE_DATA *     file_New(
    )
    {
        FILE_DATA       *this;
        
        // Do initialization.
        
        this = file_Alloc( );
        if (this) {
            this = file_Init(this);
        }
        
        // Return to caller.
        return this;
    }
    
    
    
    //---------------------------------------------------------------
    //                   D e l e t e
