    ERESULT         file_DeleteA(
        const
        char            *pPath
    )
    {
        ERESULT         eRc = ERESULT_GENERAL_FAILURE;
#if     defined(__MACOSX_ENV__)
        struct stat     statBuffer;
        int             iRc;
#endif
        
        if (NULL == pPath) {
            DEBUG_BREAK();
            return ERESULT_INVALID_PARAMETER;
        }

#if     defined(__MACOSX_ENV__)
        iRc = stat(pPath, &statBuffer);
        if (0 == iRc) {
            iRc = unlink(pPath);
            if (0 == iRc) {
                eRc = ERESULT_SUCCESS;
            }
            else {
                eRc = ERESULT_FILE_OPERATION_FAILED;
            }
        }
        else {
            eRc = ERESULT_FILE_NOT_FOUND;
        }
#endif
        
        // Return to caller.
        return eRc;
    }
    
    
    
    //---------------------------------------------------------------
    //                       F i l e  S i z e A
    //---------------------------------------------------------------
    
    int64_t         file_SizeA(
        const
        char            *pPath
    )
    {
#if defined(__MACOSX_ENV__)
        struct stat     statBuffer;
        int             iRc;
#endif
        int64_t         fileSize = -1;

        if (NULL == pPath) {
            return -1;
        }
        
#if defined(__MACOSX_ENV__)
        iRc = stat(pPath, &statBuffer);
        if (0 == iRc) {
            if ((statBuffer.st_mode & S_IFMT) == S_IFREG) {
                fileSize = statBuffer.st_size;
            }
        }
#endif
        
        // Return to caller.
        return fileSize;
    }
    
    
    
    
    //---------------------------------------------------------------
    //                   F i l e  T o  A r r a y A
    //---------------------------------------------------------------
    
    ERESULT         file_ToArrayA(
        const
        char            *pPath,
        U8ARRAY_DATA    **ppArray
    )
    {
        U8ARRAY_DATA    *pArray;
        int64_t         size = 0;
        int             chr;
        FILE            *pFile;
        
        size = file_SizeA(pPath);
        if (-1 == size) {
            return ERESULT_DATA_NOT_FOUND;
        }
        if (0 == size) {
            return ERESULT_DATA_NOT_FOUND;
        }
        pArray = u8Array_New();
        if (OBJ_NIL == pArray) {
            return ERESULT_INSUFFICIENT_MEMORY;
        }

        pFile = fopen(pPath, "rb");
        if (NULL == pFile) {
            return ERESULT_FILE_NOT_FOUND;
        }
        while ( (chr = fgetc(pFile)) != EOF ) {
            u8Array_AppendData(pArray, (chr & 0xFF));
        }
        fclose(pFile);
        pFile = NULL;
        
        // Return to caller.
        if (ppArray) {
            *ppArray = pArray;
        }
        return ERESULT_SUCCESS;
    }
    
    
    
    //---------------------------------------------------------------
    //                   F i l e  T o  B u f f e r A
    //---------------------------------------------------------------
    
    ERESULT         file_ToBufferA(
        const
        char            *pPath,
        uint8_t         **ppBuffer
    )
    {
        //ERESULT         eRc;
        uint8_t         *pBuffer = NULL;
        int64_t         size;
        int             chr;
        FILE            *pFile;
        uint8_t         *pData;
        
        if (ppBuffer) {
            *ppBuffer = pBuffer;
        }
        size = file_SizeA(pPath);
        if (-1 == size) {
            return ERESULT_DATA_NOT_FOUND;
        }
        if (0 == size) {
            return ERESULT_DATA_NOT_FOUND;
        }
        pBuffer = mem_Malloc(size);
        if (OBJ_NIL == pBuffer) {
            return ERESULT_INSUFFICIENT_MEMORY;
        }
        
        pFile = fopen(pPath, "rb");
        if (NULL == pFile) {
            return ERESULT_FILE_NOT_FOUND;
        }
        pData = pBuffer;
        while ( (chr = fgetc(pFile)) != EOF ) {
            *pData++ = (chr & 0xFF);
        }
        fclose(pFile);
        pFile = NULL;
        
        // Return to caller.
        if (ppBuffer) {
            *ppBuffer = pBuffer;
        }
        return ERESULT_SUCCESS;
    }
    
    
    
    //---------------------------------------------------------------
    //                       T o u c h A
    //---------------------------------------------------------------
    
    ERESULT         file_TouchA(
        const
        char            *pPath
    )
    {
#if defined(__MACOSX_ENV__)
        struct stat     statBuffer;
        int             iRc;
#endif
        ERESULT         eRc = ERESULT_GENERAL_FAILURE;
        
        if (NULL == pPath) {
            return ERESULT_INVALID_PARAMETER;
        }
        
#if defined(__MACOSX_ENV__)
        iRc = stat(pPath, &statBuffer);
        if (0 == iRc) {
            if ((statBuffer.st_mode & S_IFMT) == S_IFREG) {
                iRc = utimes(pPath, NULL);
                if (0 == iRc) {
                    eRc = ERESULT_SUCCESS;
                }
            }
            else
                eRc = ERESULT_FAILURE_FALSE;
        }
        else {
            if (iRc == ENOENT) {
                ;
            }
            eRc = ERESULT_PATH_NOT_FOUND;
        }
#endif
        
        // Return to caller.
        return eRc;
    }
    
    
    
    
    

