    //----------------------------------------------------------
    //                          A d d
    //----------------------------------------------------------
    
    bool            hash32_Add(
        HASH32_DATA     *cbp,
        uint32_t        key,
        void			*pData
    )
    {
        HASH32_NODE     *pNode;
        uint16_t        index;
        
#ifdef NDEBUG
#else
        if( !hash32_Validate( cbp ) ) {
            DEBUG_BREAK();
            return false;
        }
#endif
        
        if (hash32_FindEntry(cbp, key)) {
            return false;
        }
        
        // Determine the entry number.
        if ( hash32_AddBlock(cbp) )
            ;
        else {
            return false;
        }
        pNode = cbp->pDeletedHead;
        cbp->pDeletedHead = pNode->pNext;
        
        // Add it to the table.
        pNode->key = key;
        pNode->pData = pData;
        index = hash32_IndexFromKey( cbp, key );
        pNode->pNext = cbp->pHash[index];
        cbp->pHash[index] = pNode;
        ++cbp->num;
        
        // Return to caller.
        return true;
    }
    
    
        
    //---------------------------------------------------------------
    //                        D e a l l o c
    //---------------------------------------------------------------

    void            hash32_Dealloc(
        OBJ_ID          objId
    )
    {
        HASH32_DATA     *cbp = objId;
        HASH32_BLOCK    *pBlock;
        HASH32_BLOCK    *pBlockNext;

        // Do initialization.
        if (NULL == cbp) {
            return;
        }        
#ifdef NDEBUG
#else
        if( !hash32_Validate( cbp ) ) {
            DEBUG_BREAK();
            return;
        }
#endif

        if( cbp->pBlocks ) {
            pBlock = cbp->pBlocks;
            while (pBlock) {
                pBlockNext = pBlock->pNext;
                mem_Free( pBlock );
                pBlock = pBlockNext;
            }
        }
        
        if( cbp->pHash ) {
            mem_Free( cbp->pHash );
            cbp->pHash = NULL;
        }
        
        obj_Dealloc( cbp );
        cbp = NULL;

        // Return to caller.
    }



    //----------------------------------------------------------
    //					  D e l e t e
    //----------------------------------------------------------
    
    /* Delete() closes down the file, deletes it and frees all
     * memory used.  It is used in lieu of HASH_Close().
     */
    
    bool            hash32_Delete(
        HASH32_DATA     *cbp,
        uint32_t        key
    )
    {
        HASH32_NODE     *pNode;
        HASH32_NODE     *pNodePrev = OBJ_NIL;
        uint16_t        index;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !hash32_Validate( cbp ) ) {
            DEBUG_BREAK();
            return 0;
        }
#endif
        
        index = hash32_IndexFromKey( cbp, key );
        if (cbp->pHash[index]) {
            pNode = cbp->pHash[index];
            if (pNode->key == key) {
                cbp->pHash[index] = pNode->pNext;
                pNode->pNext = cbp->pDeletedHead;
                cbp->pDeletedHead = pNode;
                --cbp->num;
                return true;
            }
        }
        else {
            return false;
        }
        while (pNode && (pNode->key != key)) {
            pNodePrev = pNode;
            pNode = pNode->pNext;
        }
        
        if (NULL == pNode) {
            return false;
        }
        
        // Delete the node.
        pNodePrev->pNext = pNode->pNext;
        pNode->pNext = cbp->pDeletedHead;
        cbp->pDeletedHead = pNode;
        --cbp->num;
        
        // Return to caller.
        return true;
    }
    
    
    
    //----------------------------------------------------------
    //                        F i n d
    //----------------------------------------------------------
    
    void *          hash32_Find(
        HASH32_DATA     *cbp,
        uint32_t        key
    )
    {
        HASH32_NODE     *pNode;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !hash32_Validate( cbp ) ) {
            DEBUG_BREAK();
            return NULL;
        }
#endif
        
        pNode = hash32_FindEntry( cbp, key );
        if (pNode) {
            return pNode->pData;
        }
        
        // Return to caller.
        return NULL;
    }
    
    
    
    //---------------------------------------------------------------
    //                          I n i t
    //---------------------------------------------------------------

    HASH32_DATA *   hash32_Init(
        HASH32_DATA     *cbp,
        uint16_t        cHash           // Hash Table Size (Prime Number)
    )
    {
        uint16_t        cbSize;
        
        if (OBJ_NIL == cbp) {
            return OBJ_NIL;
        }
        
        cbp = obj_Init( cbp, obj_getSize(cbp), OBJ_IDENT_HASH32 );
        if (OBJ_NIL == cbp) {
            return OBJ_NIL;
        }
        obj_setVtbl(cbp, &hash32_Vtbl);
        
        // Initialize the fields.
        cbSize = 4096 - sizeof(HASH32_BLOCK);
        cbSize /= sizeof(HASH32_NODE);
        cbp->cBlock = cbSize;
        cbp->cHash = cHash;
        
        // Allocate the Hash Table.
        cbSize = cHash * sizeof(HASH32_NODE *);
        cbp->pHash = (HASH32_NODE **)mem_Malloc( cbSize );
        if(NULL == cbp->pHash) {
            DEBUG_BREAK();
            obj_Release(cbp);
            cbp = OBJ_NIL;
            return cbp;
        }
        memset(cbp->pHash, 0, cbSize);
        
#ifdef NDEBUG
    #else
        if( !hash32_Validate( cbp ) ) {
            DEBUG_BREAK();
            return OBJ_NIL;
        }
        //BREAK_NOT_BOUNDARY4(&cbp->thread);
    #endif

        return cbp;
    }

     

    //----------------------------------------------------------
    //					  U p d a t e
    //----------------------------------------------------------
    
    bool            hash32_Update(
        HASH32_DATA     *cbp,
        uint32_t        key,
        void            *pData
    )
    {
        HASH32_NODE     *pNode;
        
        // Do initialization.
#ifdef NDEBUG
#else
        if( !hash32_Validate( cbp ) ) {
            DEBUG_BREAK();
            return 0;
        }
#endif
        
        pNode = hash32_FindEntry( cbp, key );
        if (pNode) {
            pNode->pData = pData;
        }
        else
            return false;
        
        // Return to caller.
        return true;
    }
    
    
    
    //---------------------------------------------------------------
    //                      V a l i d a t e
    //---------------------------------------------------------------

    #ifdef NDEBUG
    #else
    bool            hash32_Validate(
        HASH32_DATA      *cbp
    )
    {
        if( cbp ) {
            if ( obj_IsKindOf(cbp,OBJ_IDENT_HASH32) )
                ;
            else
                return false;
        }
        else
            return false;
        if( !(obj_getSize(cbp) >= sizeof(HASH32_DATA)) )
            return false;

        // Return to caller.
        return true;
    }
    #endif


    
    
    
